package com.javatest.leetcode;


import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

//Given two strings s and p, return
// an array of all the start indices of p's anagrams in s.
//You may return the answer in any order.
//
//An Anagram is a word or phrase formed by rearranging
// the letters of a different word or phrase, typically
// using all the original letters exactly once.
public class FindAllAnagramsinaString {

    public static List<Integer> findAnagrams(String s, String p) {

        List<Integer> result = new ArrayList<>();

        if (s.equals(p)) {
            result.add(1);
            return result;
        }
        Map<Character, Integer> map = new HashMap<>();
        for (int i = 0; i < p.length(); i++) {
            map.put(p.charAt(i), map.getOrDefault(p.charAt(i), 0) + 1);
        }

        for (int i = 0; i < s.length(); i++) {
            if (check(map, s, i)) result.add(i);
        }

        return result;
    }

    public static boolean check(Map<Character, Integer> map, String s, int i) {
        Map<Character, Integer> temp = new HashMap<>(map);
        int index = i;
        while (!temp.isEmpty()) {
            if (temp.containsKey(s.charAt(index))) {
                temp.put(s.charAt(index), temp.get(s.charAt(index)) - 1);
                if (temp.get(s.charAt(index)) == 0) temp.remove(s.charAt(index));
                index++;
                if (index == s.length() || temp.isEmpty()) break;
            } else return false;
        }
        if (!temp.isEmpty()) return false;
        return true;
    }

    public static void main(String[] args) {
        System.out.println(findAnagrams("", "ab"));
    }


}

class Solution2 {
    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> res = new ArrayList<>();
        Map<Character, Integer> pHash = new HashMap<>();
        Map<Character, Integer> hash = new HashMap<>();
        for (int i = 0; i < p.length(); i++) {
            pHash.put(p.charAt(i), pHash.getOrDefault(p.charAt(i), 0) + 1);
        }
        int left = 0, right = 0;
        while (right < s.length()) {
            char c = s.charAt(right);
            hash.put(c, hash.getOrDefault(c, 0) + 1);
            right++;
            if (pHash.equals(hash)) {
                res.add(left);
            }
            if (right - left == p.length()) {
                char leftChar = s.charAt(left);
                if (hash.containsKey(leftChar)) {
                    hash.put(leftChar, hash.get(leftChar) - 1);
                    if (hash.get(leftChar) == 0) {
                        hash.remove(leftChar);
                    }
                }
                left++;
            }
        }

        return res;
    }
}